mapper.update:
update tb_announcement
set title       = #{title},
    content     = #{content},
    writer      = #{writer},
    create_time = #{createTime}
where id = #{id};

mapper.select:
select id, title, content, writer, create_time
from tb_announcement
where title like #{title}
order by create_time desc
limit #{begin},10;

select id, title, content, writer, create_time
from tb_announcement
where id = #{id};

mapper.delete:
delete
from tb_announcement where id in

    #{id}

mapper.select:
select id, train_name, train_location, description, start_date, end_date
from tb_train
where train_name like #{trainName}
order by start_date desc, end_date desc
limit #{begin},10;

select count(*)
from tb_train
where train_name like #{trainName}
order by start_date desc, end_date desc;

select id, train_name, train_location, description, start_date, end_date
from tb_train
where id = #{id};
