select t1.id, employee_id,t2.name,t2.department,t2.role,quit_reason, quit_status
from tb_quit as t1 join tb_employee as t2
on t1.employee_id = t2.id


        and quit_status != '同意'


        and quit_reason like #{reason}


limit #{begin},10;

select count(*)
from tb_quit


        and employee_id = #{employeeId}


        and quit_status != '同意'


        and quit_reason like #{reason}


limit #{begin},10;

